import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import { Button, Card, Menu } from 'semantic-ui-react';
import * as ROUTES from '../../constants/routes';
import Content from '../Content';

import {
  AuthUserContext,
} from '../Session';

class PostPage extends React.Component {

	render() {
		return (
			<div>
				<Button basic color='red' as={Link} to={ROUTES.RESET}>
					Reset Password
				</Button>
				<Button basic color='green' as={Link} to={ROUTES.POST}>
					Post Book
				</Button>
			</div>
		)
	}
}

const AccountPage = () => (
  <AuthUserContext.Consumer>
    {authUser => (
      <div>
        <Card>
          <Card.Content>
            <Card.Header>Hi {authUser.username}</Card.Header>
          </Card.Content>
          <Card.Content extra>
            <PostPage />
          </Card.Content>
        </Card>

        <Menu tabular>
          <Menu.Item name='bio' active={activeItem === 'bio'}  />
          <Menu.Item name='photos' active={activeItem === 'photos'} />
        </Menu>

        <Content />
      </div>
    )}
  </AuthUserContext.Consumer>
);



export default AccountPage;
