import React from 'react'
import { Grid, Image, Icon, Segment, Header } from 'semantic-ui-react'
import Image1 from './image/img1.png';
import Image2 from './image/img2.jpg';
import axios from 'axios'

class GridTemp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: this.props.page,
            activeItem: this.props.activeItem,
            list: []
        };
        this.detailClickHandler = this.detailClickHandler.bind(this);

        axios.get('http://localhost:8000/api/books').then(async (res) => {

            var temp = await res.data.data.sort((a, b) => {
                if(this.state.activeItem == "Newly Added" && a.dateCreated > b.dateCreated) {
                    return -1;
                }
                if(this.state.activeItem == "Newly Added" && a.dateCreated <= b.dateCreated) {
                    return 1;
                }
                if(this.state.activeItem == "Most Viewed" && a.watchedUsers.length <= b.watchedUsers.length) {
                    return 1;
                }
                if(this.state.activeItem == "Most Viewed" && a.watchedUsers.length > b.watchedUsers.length) {
                    return -1;
                }

            })
            console.log(res.data.data);
            console.log(temp);
            this.setState({
                list: temp.map((book) => {
                    return (
                        <Grid.Column>
                        <Segment>
                        <Image onClick={() => this.detailClickHandler(book._id)} src={book.picture} />
                            <p>
                            Title: {book.title}
                            <br/>
                            <br/>
                            Description: {book.description}
                            </p>
                        </Segment>
                        <br/>
                        </Grid.Column>
                        )
                })
            })
        })
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
          page: nextProps.page,

        });
      }

      detailClickHandler(value) {
        window.location.href = 'http://localhost:3000/details/' + value;
      }

    componentDidMount() {
        console.log(this.state.activeItem);

    }

    render(){
        return(
        <Grid stackable columns={3}>
        {console.log("Child page state: ", this.state.page)}
        <Grid.Row>
        {this.state.list}
        </Grid.Row>
    </Grid>
    )
}
}

export default GridTemp
