import React from 'react';
import {Input, Search, Grid, Image, Icon, Header, Menu, activeItem } from 'semantic-ui-react';
import GridTemp from './grid';
import Background from './image/backg.jpg';

var sectionStyle = {
	backgroundImage: `url(${Background})`
  };

class Landing extends React.Component {

	constructor(){
		super();
		this.state = {
			activeItem: 'Newly Added'
		}
		this.newHandleItemClick = this.newHandleItemClick.bind(this);
		this.mostHandleItemClick = this.mostHandleItemClick.bind(this);
		this.searchHandler = this.searchHandler.bind(this);

	}

	newHandleItemClick() {
		this.setState({ activeItem: 'Newly Added' })
		// console.log("Parent page state: ", this.state.activeItem)
	}

	mostHandleItemClick() {
		this.setState({ activeItem: 'Most Viewed' })
		// this.render();
		// console.log("Parent page state: ", this.state.activeItem)
	}

	searchHandler(e) {
		this.setState({
			query: e.target.value
		})
	}

	render(){
		return (
			<div>
				<Menu pointing secondary size='huge' stackable>
				<Menu.Item 
					name='Newly Added' 
					onClick={this.newHandleItemClick}
				/>
				<Menu.Item
					name='Most Viewed'
					onClick={this.mostHandleItemClick}
				/>
				<div onChange={this.searchHandler} className = "center item" id="searchBar">
		          <Input className="searchBar"/>
		        </div>
				</Menu>
				<GridTemp query={this.state.query} activeItem={this.state.activeItem} page={this.state.activeItem}/>
			</div>
		)
	}

};

export default Landing;
