import React from 'react'
import { Grid, Image, Icon, Segment, Header } from 'semantic-ui-react'
import Image1 from './image/img1.png';
import Image2 from './image/img2.jpg';
import axios from 'axios'

class GridTemp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: this.props.page,
            activeItem: this.props.activeItem,
            list: []
        };
        this.detailClickHandler = this.detailClickHandler.bind(this);
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
          page: nextProps.page
        });
      }

      detailClickHandler(value) {
        window.location.href = 'http://localhost:3000/details/' + value;
      }

    componentDidMount() {
        axios.get('http://localhost:8000/api/books').then((res) => {
            var temp = res.data.data;
            temp.sort((a, b) => {
                if(this.state.activeItem == "Newly Added" && a.dateCreated.getTime() > b.dateCreated.getTime()) {
                    return 1;
                }
                if(this.state.activeItem == "Newly Added" && a.dateCreated.getTime() <= b.dateCreated.getTime()) {
                    return -1;
                }

            })

            this.setState({
                list: res.data.data.map((book) => {
                    return (
                        <Grid.Column>
                        <Segment>
                        <Image onClick={() => this.detailClickHandler(book._id)} src={book.picture} />
                            <p>
                            Title: {book.title}
                            <br/>
                            <br/>
                            Description: {book.description}
                            </p>
                        </Segment>
                        <br/>
                        </Grid.Column>
                        )
                })
            })
        })
    }

    render(){
        return(
        <Grid stackable columns={3}>
        {console.log("Child page state: ", this.state.page)}
        <Grid.Row>
        {this.state.list}
        </Grid.Row>
    </Grid>
    )
}
}

export default GridTemp
