import React from 'react';
import { Container, Card, Icon, Image } from 'semantic-ui-react';
import Image1 from '../Landing/image/img1.png';
import './Detailed.scss';

class Detailed extends React.Component {

	state = { activeItem: 'General' }

	handleItemClick = (e, { name }) => {
		this.setState({ activeItem: name })
		// console.log("Parent page state: ", this.state.activeItem)
	}

	render(){
		return (
			<div>
                <Container textAlign='center'>
                <Card id='card'>
                    <Image src={Image1} size='massive' />
                    <Card.Content>
                    <Card.Header>CS 101</Card.Header>
                    <Card.Meta>
                        <span className='price'>Price: $15.9</span>
                    </Card.Meta>
                    <Card.Meta>
                    <span className='owner'>Owner: Yun Ma</span>
                    </Card.Meta>
                    <Card.Description>Discription XXX</Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                    <a>
                        <Icon name='user' />
                        22 Others want to purchase
                    </a>
                    </Card.Content>
                </Card>
                </Container>
			</div>
		)
	}

};

export default Detailed;
