import React from 'react';
import { Grid, Image, Icon, Header, Menu, activeItem } from 'semantic-ui-react';
import GridTemp from './grid';
import Background from './image/backg.jpg';

var sectionStyle = {
	backgroundImage: `url(${Background})`
  };

class Landing extends React.Component {

	state = { activeItem: 'General' }

	handleItemClick = (e, { name }) => {
		this.setState({ activeItem: name })
		// console.log("Parent page state: ", this.state.activeItem)
	}

	render(){
		return (
			<div style = {sectionStyle}>
				<Menu pointing secondary size='huge' stackable>
				<Menu.Item 
					name='General' 
					onClick={this.handleItemClick}
				/>
				<Menu.Item
					name='Most Viewed'
					onClick={this.handleItemClick}
				/>
				<Menu.Item
					name='Hot Trend'
					onClick={this.handleItemClick}
				/>
				</Menu>
				<GridTemp page={this.state.activeItem}/>
			</div>
		)
	}

};

export default Landing;
