import React from 'react';
import { Link } from 'react-router-dom';
import { Search } from 'semantic-ui-react'

import { AuthUserContext } from '../Session';
import SignOutButton from '../SignOut';
import * as ROUTES from '../../constants/routes';
import * as ROLES from '../../constants/roles';

// import './Navbar.scss'
const Navigation = () => (
  <AuthUserContext.Consumer>
	{authUser =>
	  authUser ? (
		<NavigationAuth authUser={authUser} />
	  ) : (
		<NavigationNonAuth />
	  )
	}
  </AuthUserContext.Consumer>
);

const NavigationAuth = ({ authUser }) => (
	<div className="ui large top fixed hidden menu" id="header">
		<div className="ui inverted vertical masthead center algined segment">
			<div className="ui large secondary inverted pointing menu">
				<div id="logo">BOOOOK.COM</div>
				{/*<a className="toc item">
					<i className="sidebar icon"></i>
				</a>*/}
				<Link to={ROUTES.LANDING} className="item" id="navButton">Home</Link>
				<Link to={ROUTES.CATEGORY} className="item" id="navButton">Category</Link>
				<Link to={ROUTES.ACCOUNT} className="item" id="navButton"> Dashboard</Link>
				<div id="searchBar">
					<Search className="searchBar"/>
				</div>
				<div className="right item">
				<SignOutButton />
				</div>
			</div>
		</div>
	</div>
);

const NavigationNonAuth = () => (
	<div className="ui large top fixed hidden menu" id="header">
		<div className="ui inverted vertical masthead center algined segment">
			<div className="ui large secondary inverted pointing menu">
				<div id="logo">BOOOOK.COM</div>
				<Link to={ROUTES.LANDING} className="item" id="navButton">Landing</Link>
				<Link to={ROUTES.SIGN_IN} className="item" id="navButton">Sign In</Link>
			</div>
		</div>
	</div>
);

export default Navigation;
