import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import { Button, Card, Menu, Image} from 'semantic-ui-react';
import * as ROUTES from '../../constants/routes';
import Content from '../Content';
import './Account.scss'

import {
  AuthUserContext,
} from '../Session';

class PostPage extends React.Component {
  state = { activeItem: 'bio' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })


	render() {
    const { activeItem } = this.state

		return (
			<div>
				<Button basic color='red' as={Link} to={ROUTES.RESET}>
					Reset Password
				</Button>
				<Button basic color='green' as={Link} to={ROUTES.POST}>
					Post Book
				</Button>
			</div>
		)
	}
}

const AccountPage = () => (
  <AuthUserContext.Consumer>
    {authUser => (
      <div className="Container">
        <div className="UserCard">
          <Card>
            <Image src='https://react.semantic-ui.com/images/avatar/large/matthew.png' />
            <Card.Content>
              <Card.Header>Hi {authUser.username}</Card.Header>
            </Card.Content>
            <Card.Content extra>
              <PostPage />
            </Card.Content>
          </Card>
        </div>

        <div className="Dropdown">
          <Menu tabular>
            <Menu.Item name='Posted Book'  active={activeItem === 'bio'} onClick={this.handleItemClick}/>
            <Menu.Item name='Wished Book'   active={activeItem === 'photos'} onClick={this.handleItemClick}/>
          </Menu>
        </div>
          {/*<Content />*/}




      </div>

    )}
  </AuthUserContext.Consumer>
);



export default AccountPage;
