import React, { Component } from 'react'
import './Post.scss'
import axios from 'axios'
const INITIAL_STATE = {
  title: '',
  college: '',
  price: 0,
  description: ''
};

class Post extends Component {

  constructor() {
    super();
    this.state = { ...INITIAL_STATE };
    this.titleChangeHandler = this.titleChangeHandler.bind(this);
    this.collegeChangeHandler = this.collegeChangeHandler.bind(this);
    this.priceChangeHandler = this.priceChangeHandler.bind(this);
    this.descriptionChangeHandler = this.descriptionChangeHandler.bind(this);
    this.clickHandler = this.clickHandler.bind(this);

  }

  titleChangeHandler(e) {
    this.setState({
      title: e.target.value
    })
  }

  collegeChangeHandler(e) {
    this.setState({
      college: e.target.value
    });
    // console.log(this.state.college);
    // console.log("this.state.college");
  }

  priceChangeHandler(e) {
    this.setState({
      price: parseInt(e.target.value, 10)
    })
    // console.log(this.state.price);
  }

  descriptionChangeHandler(e) {
    this.setState({
      description: e.target.value
    })
    // console.log(this.state.description);
  }

  clickHandler(e) {
    axios.post("http://localhost:8000/api/books", {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price,
      college: this.state.college,
      assignedUser: window.sessionStorage.getItem('userId')
    }).then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  render() {
    return (
      <div className="height100">
    		<div className="height100">
        	<img src = ''/>
              <div className="inputField">
                <form className="ui form">
                  <div class="field">
                    <label>Title</label>
                    <input type="text" name="title" placeholder="Title" onChange={this.titleChangeHandler}/>
                  </div>
                  <div class="field">
                    <label>College</label>
                    <select  value={this.state.college} onChange={this.collegeChangeHandler} class="ui fluid dropdown">
                      <option>Choose..</option>

                    <option value="College of Liberal Art and Science">College of Liberal Art and Science</option>
                    <option value="College of Grainger Engineering">College of Grainger Engineering</option>
                    <option value="College of Business">College of Business</option>
                    <option value="College of Education">College of Education</option>
                    <option value="College of Medicine">College of Medicine</option>
                    <option value="College of Law">College of Law</option>
                    </select>
                  </div>


                  <div class="field">
                    <label>Price</label>
                    <div class="ui labeled input">
                      <label for="amount" class="ui label">$</label>
                      <input onChange={this.priceChangeHandler} type="text" placeholder="Amount" id="amount" />
                    </div>
                  </div>

                  <div class="ui reply form">
                    <div class="field">
                      <label>Description</label>
                      <textarea onChange={this.descriptionChangeHandler}></textarea>
                    </div>
                  </div>

                  <br/>

                  <div class="field">
                    <label>Upload Picture</label>
                    <input type="file" change="fileEvent($event)" class="inputfile" />
                  </div>


                  <br/>


                  <button onClick={this.clickHandler} class="ui button">Post</button>
                </form>
              </div>
        </div>
        </div>

    )
  }

}
export default Post
