import React from 'react';
import { Button, Container, Card, Icon, Image } from 'semantic-ui-react';
import Image1 from '../Landing/image/img1.png';
import './Detailed.scss';
import axios from 'axios'


class Detailed extends React.Component {

    constructor(){
        super();
        this.state = {
        activeItem: 'General',
        price: 0,
        title: '',
        college: '',
        description: '',
        assignedUser: '',
        owner: '',
        watchedUser: 0,
        picture: ''
    };
    this.buyClickHandler = this.buyClickHandler.bind(this);
    }

    buyClickHandler() {

    }

	handleItemClick = (e, { name }) => {
		this.setState({ activeItem: name })
		// console.log("Parent page state: ", this.state.activeItem)
	}

    componentDidMount() {
        console.log(this.props)
        axios.get('http://localhost:8000/api/books/' + this.props.match.params.id).then((res) => {
            this.setState({
                price: res.data.data.price,
                title: res.data.data.title,
                college: res.data.data.college,
                description: res.data.data.description,
                watchedUser: res.data.data.watchedUsers.length,
                picture: res.data.data.picture
            });
            console.log(res.data.data)
            axios.get('http://localhost:8000/api/users/' + res.data.data.assignedUser).then((res2) => {
                this.setState({
                    owner: res2.data.data.name
                })
                console.log(this.state.owner);
            })
        })
    }

	render(){
		return (
			<div>
                <Container textAlign='center'>
                <Card id='card'>
                    <Image src={this.state.picture} size='massive' />
                    <Card.Content>
                    <Card.Header>{this.state.title}</Card.Header>
                    <Card.Meta>
                        <span className='price'>Price: ${this.state.price}</span>
                    </Card.Meta>
                    <Card.Meta>
                        <span className='owner'>Owner: {this.state.owner}</span>
                    </Card.Meta>
                    <Card.Description>{this.state.description}</Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                    <a>
                        <Icon name='user' />
                        {this.state.watchedUser} Others want to purchase
                    </a>
                    </Card.Content>
                </Card>
                <br/>
                <Button onClick={this.buyClickHandler}>
                 BUY with one click!
                </Button>
                </Container>
			</div>
		)
	}

};

export default Detailed;
