import React, { Component } from 'react';
import { Grid, Image, Segment } from 'semantic-ui-react'
import axios from 'axios'

class Content extends Component {
	constructor() {
		super()
		this.state = {
			wish: [],
			post: []
		};
		this.detailClickHandler = this.detailClickHandler.bind(this);
    }


    componentDidMount() {
		var id = window.sessionStorage.getItem('userId');

		var user = 'http://localhost:8000/api/users/';
		var book = 'http://localhost:8000/api/books/';

		var tempName = []
		var tempBook = []
		axios.get(user + id).then((res) => {
			tempName = res.data.data.wishedBooks

			for (var i = 0; i < tempName.length; i++) {
				axios.get(book + tempName[i]).then(async (res) => {
					var bk = await res.data.data
					tempBook.push(bk)
				})
			}
			return tempBook;
		}).then(async (list) => {
			console.log(list);
			this.setState({
                wish: await list.map((bk) => {
                    return (
                        <Grid.Column>
                        <Segment>
                        <Image onClick={() => this.detailClickHandler(bk._id)} src={bk.picture} />
                            <p>
                            Title: {bk.title}
                            <br/>
                            <br/>
                            College: {bk.college}
                            <br/>
                            <br/>
                            Description: {bk.description}
                            </p>
                        </Segment>
                        <br/>
                        </Grid.Column>
                    )
                })
            })
        })

		axios.get(user + id).then(async (res) => {
			tempName = await res.data.data.postedBooks

			for (var i = 0; i < tempName.length; i++) {
				axios.get(book + tempName[i]).then(async (res) => {
					var bk = await res.data.data
					tempBook.push(bk)
				})
			}

			this.setState({
                post: tempBook.map((bk) => {
					return (
						<Grid.Column>
	                        <Segment>
	                        <Image onClick={() => this.detailClickHandler(bk._id)} src={bk.picture} />
	                            <p>
	                            Title: {bk.title}
	                            <br/>
	                            <br/>
	                            College: {bk.college}
	                            <br/>
	                            <br/>
	                            Description: {bk.description}
	                            </p>
	                        </Segment>
	                        <br/>
	                    </Grid.Column>
	                )
	            })
            })
            console.log(tempBook)
            console.log(this.state.post)
        })
    }

	detailClickHandler(value) {
        window.location.href = 'http://localhost:3000/details/' + value;
    }

    render() {
        return(
       	<div>
       		<h2> Posted Books </h2>
	        <Grid stackable columns={3}>
		        <Grid.Row>
		        {this.state.post}
		        </Grid.Row>
	    	</Grid>
	    	<h2> Wished Books </h2>
	        <Grid stackable columns={3}>
		        <Grid.Row>
		        {this.state.wish}
		        </Grid.Row>
	    	</Grid>
	    </div>
    	)
    }
}

export default Content;
